import CoreGraphics
import Foundation
import Nimble
import Quick
@testable import TPPDF

class PDFGraphics_Spec: QuickSpec {
    override func spec() {
        describe("PDFGraphics") {
            it("can create dashes with butts") {
                let width: CGFloat = 5
                let style = PDFLineStyle(type: .dashed, color: .red, width: width)

                let dashes = PDFGraphics.createDashes(style: style)
                expect(dashes?.lengths) == [3 * width, 3 * width]
                expect(dashes?.cap) == CGLineCap.butt
            }

            it("can create dotted dashes") {
                let width: CGFloat = 5
                let style = PDFLineStyle(type: .dotted, color: .red, width: width)

                let dashes = PDFGraphics.createDashes(style: style)
                expect(dashes?.lengths) == [0, 2 * width]
                expect(dashes?.cap) == CGLineCap.round
            }

            it("does nothing when not a dashed type") {
                let width: CGFloat = 5
                let style = PDFLineStyle(type: .none, color: .red, width: width)

                let dashes = PDFGraphics.createDashes(style: style)
                expect(dashes).to(beNil())
            }

            describe("resizing and compression") {
                let base64String = ""

                let data: Data! = Data(base64Encoded: base64String)
                expect(data).toNot(beNil())

                var image: Image!
                let resultFrame = CGRect(x: 0, y: 0, width: 40, height: 40)

                beforeEach {
                    image = Image(data: data)
                    expect(image).toNot(beNil())

                    expect(image.size) == CGSize(width: 61, height: 68)
                }

                it("can resize image") {
                    let result = PDFGraphics.resizeAndCompressImage(image: image,
                                                                    frame: resultFrame,
                                                                    shouldResize: true,
                                                                    shouldCompress: false,
                                                                    quality: 0.2)
                    expect(result) !== image
                    expect(result.size.width).to(beCloseTo(24))
                    expect(result.size.height).to(beCloseTo(24))
                }

                it("can compress image") {
                    let result = PDFGraphics.resizeAndCompressImage(image: image,
                                                                    frame: resultFrame,
                                                                    shouldResize: false,
                                                                    shouldCompress: true,
                                                                    quality: 0.2)
                    expect(result) !== image
                    expect(result.size) == image.size
                }

                it("can resize and compress image") {
                    let result = PDFGraphics.resizeAndCompressImage(image: image,
                                                                    frame: resultFrame,
                                                                    shouldResize: true,
                                                                    shouldCompress: true,
                                                                    quality: 0.2)
                    expect(result) !== image
                    expect(result.size) == CGSize(width: 24.0, height: 24.0)
                }

                it("can return original image") {
                    let result = PDFGraphics.resizeAndCompressImage(image: image,
                                                                    frame: resultFrame,
                                                                    shouldResize: false,
                                                                    shouldCompress: false,
                                                                    quality: 0.2)
                    expect(result) === image
                }

                it("can compress an image") {
                    let result = PDFGraphics.resizeAndCompressImage(image: image,
                                                                    frame: resultFrame,
                                                                    shouldResize: true,
                                                                    shouldCompress: true,
                                                                    quality: 0.0)
                    expect(result) !== image
                }

                it("can resize and compress an image") {
                    let result = PDFGraphics.resizeAndCompressImage(image: image,
                                                                    frame: resultFrame,
                                                                    shouldResize: true,
                                                                    shouldCompress: true,
                                                                    quality: 0.0)
                    expect(result.size) == CGSize(width: 8, height: 8)
                }

                it("can create a rounded image") {
                    let result = PDFGraphics.resizeAndCompressImage(image: image,
                                                                    frame: resultFrame,
                                                                    shouldResize: false,
                                                                    shouldCompress: false,
                                                                    quality: 0.0,
                                                                    roundCorners: [.allCorners],
                                                                    cornerRadius: 10)
                    expect(result) !== image
                }
            }
        }
    }

    static var allTests = [
        ("spec", spec),
    ]
}
